(window.webpackJsonp=window.webpackJsonp||[]).push([[39],{328:function(v,_,p){"use strict";p.r(_);var t=p(14),s=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"关于朱令事件的几点说明"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#关于朱令事件的几点说明"}},[v._v("#")]),v._v(" 关于朱令事件的几点说明")]),v._v(" "),_("blockquote",[_("p",[v._v("贝志城")])]),v._v(" "),_("p",[v._v("我是贝志城，朱令的初中高中同学。朱令大学同学们口中的“谣言”制造者。1995 年 4 月，朱令二次中毒，4 月 8 日我和 5 名中学同学一起去医院看望她，我们每次一个进入 ICU，那个我们熟悉的美丽、活泼、多才多艺的朱令，头发全秃，全身插满管子躺在那里，昏迷不醒。我现在还记得自己当时的感觉，双腿发软，想跑又跑不动。一个男同学说，我们一定要救朱令。那时我刚刚接触互联网，就和朱令的父母说了，要通过互联网求助，确定朱令的病因。朱令的父母对互联网一无所知，并没有表示出很大的兴趣。")]),v._v(" "),_("p",[v._v("1995 年 4 月 10 日我们开始通过互联网求助，就此我开始卷入此事。")]),v._v(" "),_("p",[v._v("我第一次在网上明确表示怀疑孙维是 2002 年，在这之前我甚至没有在网上谈论过，因为这是一个非常不愉快地回忆。不知道朱令和孙维的大学同学们说我每隔两三年就散布“谣言”有何根据。")]),v._v(" "),_("p",[v._v("怀疑孙维并不是我的臆断，1995 年之前，我根本不知道孙维是谁。朱令铊中毒距现在已经 11 年了，警方透露给朱令家属的唯一嫌疑人，就是孙维。并不是我以及朱令家属怀疑孙维，警方才开始调查孙维，而是警方长时间地调查孙维，我们才知道了孙维是这个案件的嫌疑人。")]),v._v(" "),_("p",[v._v("朱令的大学同学们，都表达了自己的美好祝愿，愿朱令活下去，健康起来。但你们可能忽略了，朱令和其亲属还有另一个愿望，那就是要知道到底谁是真凶。")]),v._v(" "),_("p",[v._v("2005 年的时候，朱令的妈妈还去市公安局信访，市局口头答复，此案上级早有批示，不可能再查了。")]),v._v(" "),_("p",[v._v("对于这个 11 年未破的案件，孙维是唯一的也是最后的线索了。")]),v._v(" "),_("p",[v._v("所以负责任的做法是说出自己知道的真相，哪怕是点点滴滴。你可以不怀疑孙维，但你用什么担保就不是孙维。我坚持自己的怀疑，从警方和清华透漏的点滴信息没法不让我坚持这个怀疑。")]),v._v(" "),_("p",[v._v("我曾经试图和朱令的大学同学联系，希望尽量接近真相，找到真凶，也不希望冤枉好人，得到的答复大多是不知情、不爱讲。现在我被群起攻之指责为“谣言”制造者，那么如果果真是谣言，你们的沉默和冷淡是不是也是这“谣言”产生的一个因素呢？")]),v._v(" "),_("p",[v._v("下面我就我所掌握的事实做以下说明，鉴于国内的现实和对知情人的保护，请恕我不能如“团支书”所要求的那样给出消息所闻的明确出处，但不代表这仅仅是道听途说。")]),v._v(" "),_("p",[v._v("1 孙维如何被怀疑？")]),v._v(" "),_("p",[v._v("调查之初，没有人怀疑孙维，朱令的同班同学都没有怀疑孙维。而是班上另两个女同学，跟朱令有矛盾，甚至在朱令重病时都坚决不去看望。包括朱令的男友当时怀疑的也是别人。")]),v._v(" "),_("p",[_("strong",[v._v("警方把孙维列为嫌疑人，是因为清华大学出具的材料：孙维是唯一接触铊的学生。民乐队，她是朱令的替补。")])]),v._v(" "),_("p",[v._v("这并非是我造的谣言，这个孙维应该很清楚，被警方问讯时，应该已被告知。")]),v._v(" "),_("p",[v._v("2 朱令的杯子在床下孙维的箱子中翻出")]),v._v(" "),_("p",[v._v("这个事实我第一次得知是 1998 年，朱令的母亲亲口所说。")]),v._v(" "),_("p",[v._v("消息来源是市公安局的一位离休干部。为朱家世交。太阳正暖只能证明派出所取走宿舍内属于朱令的东西，并不能证明警方没有搜查过孙维的物品。")]),v._v(" "),_("p",[v._v("3 朱令父亲走私铊传言")]),v._v(" "),_("p",[v._v("中国的重重社会关系，直面很困难的，我站出来了也就准备付出代价。警方调查之初，我的一位关系很好的大学同学，女友在清华且和朱令班上一些女生关系不错。说清华传言朱令中毒是因为他爸爸走私铊，不小心沾染的。当时，我想这个谣言如此恶毒，实在不像是无聊的人可以编出来的，告知警方调查出谣言的来源有助于此案的侦查。好友因此差点和我决裂，我被讯问时警方态度很友好，他的女友被询问警方的态度就完全不一样了。我对同学很抱歉，但是这件事还是要做。同时，我补充一点，谣言的来源最后查到了，确定为孙维所为。")]),v._v(" "),_("p",[v._v("4 翻译事件")]),v._v(" "),_("p",[v._v("救助过程中，我们专门编写了一个软件分析写邮件人的严肃度（包括是否医生、他判断是那种病、回信频率），在怀疑铊中毒之后，也用关键字搜索分类，把不同的诊断方案、治疗办法分出来，最后治疗方案也是这样。所以，当我们需要朱令同学帮助翻译时，我亲历的情形上个以前帖子已有详细描述，朱令同学的表现令人心寒。第二次我的同学吴向军和她们的团委书记应该说得很清楚，第一协和不接受材料，第二翻译的结果必须我们拿回来处理和甄别后才能有用。但是我们几次催要都得不到任何翻译的结果，现在这位团委书记解释说是直接交给协和了，我相信他？不管是因为他是党员习惯循规蹈矩还是清华北大的俞亮情节，显然他把这些摆在了他的同学安危之前。")]),v._v(" "),_("p",[v._v("5 朱令的班集体")]),v._v(" "),_("p",[v._v("朱令的班集体，恕我直言，给我的感觉，很奇怪。无论是在翻译事件的所作所为，还是后面我贴出的朱令的同学给我的邮件。在我看来，都是一个重视集体荣誉超过一切甚至同学的生命的班集体。现在这么多自称是她同学的人跑上来起劲，第一我希望你们用真名发言，无论是你们想洗清孙维的怀疑还是希望找出真凶，真名发言都是效果最好的。第二，希望你们跳着脚证明孙维人品的时候，能够把这十年来你们没做的工作补做一下，清华到底对铊盐的管理是怎么样的，同学中谁能够接触。请不要在 11 年之后，告诉我，朱令是自己不小心误服的，其实那个真凶是不存在的。")]),v._v(" "),_("p",[v._v("6 关于孙维被清华扣发毕业证及不出具出国证明")]),v._v(" "),_("p",[v._v("孙维自己的帖子已经证明我所言非虚，孙维的同学的帖子也证实了她已出国多年。")]),v._v(" "),_("p",[v._v("7 孙维的爷爷有没有干涉此事")]),v._v(" "),_("p",[v._v("95 年下半年，警方已告知朱令父母在调查孙维。孙维声称警方 97 年前都没问讯过她，并以她爷爷 1995 年 12 月已经去世，证明没有涉及此事。此事的正确时间线索是，95 年下半年，警方已明确锁定孙维，当时的最高国家领导人不是邓，中国人都知道，在中国最高领导人是总书记。")]),v._v(" "),_("p",[v._v("8 我为什么坚持怀疑孙维")]),v._v(" "),_("p",[v._v("我接受警方问询仅一次。我没有向朱令家人提供过任何潜在怀疑人的信息，朱令家人对孙维的怀疑来自警方。我和朱令的父母通过不同渠道看到了一些证据，我怀疑孙维就是凶手，但不是 100% 确定。很多人希望在这里提供证据，我说了一些可以说得，但是第一中国的政治和现实不允许我提供更多的，第二很多证据我相信嫌疑人本身也不知道；我在这里提供了只会让可能的凶手掌握更多的资料，更好的逃避法律的制裁。")]),v._v(" "),_("p",[v._v("我的消息来源主要来自警方、校方提供给警方的证词，以及朱令的父母、或者是刚才那位我的同学的女友，我没有理由怀疑他们造谣。如果没有人能够提供确凿证明说明清华作了伪证，我不会收回这个怀疑。更何况一些信息是从两个不同渠道得到了证实。")]),v._v(" "),_("p",[v._v("孙维的同学有的写的言之凿凿，以前说根本不了解情况的不也是你们吗？有的证明比较可笑，例如，太阳正暖同学愤怒的证明水杯事件是子虚乌有的，其实你能说的只是，这件事我不知道。警方调查的结果不可能透露给你，警方搜查的时候也不可能让你在边上看着。包括还有一些自称同学的人居然提出来让我登出警方案卷这样天方夜谭的要求，我很不理解你们这么做的原因。")]),v._v(" "),_("p",[v._v("我希望你们提出证据的时候，不是用一堆马甲互相证明，为什么能证明，拿出证据来。你们身在国外，不存在面临的政治压力等等。无论为了孙维还是朱令都希望你们用真名站出来说清楚。")]),v._v(" "),_("p",[v._v("9 清华的责任")]),v._v(" "),_("p",[v._v("协和治疗过程中，极度抗拒外面的协助，记得我把整理好怀疑铊中毒的资料交给他们，在医院走廊等了一上午，所有医生都拒绝接。事后他们解释误诊的主要原因是由于清华，说清华矢口否认朱令可能接触到重金属盐。")]),v._v(" "),_("p",[v._v("另外由于是两次中毒高峰，当时想不到投毒的可能，没人会想到重金属中毒的症状是这样。警方调查，又是清华指证只有孙维因为参加一个项目可能接触到铊，如果孙维说不只他一个人能接触铊是真的，那我认为此事无论朱令的中毒和迟迟找不到凶手都是清华责任最大。")]),v._v(" "),_("p",[v._v("10 网上洗不白孙维")]),v._v(" "),_("p",[v._v("我不认为是我一直在造谣言，孙维彻底洗清自己的嫌疑也不应该是在网上。")]),v._v(" "),_("p",[v._v("如果朱令事件还能有个水落石出之日，我希望孙维勇敢地向校方和警方讨还自己的清白。")]),v._v(" "),_("p",[v._v("孙维的同学们，最应该做的，也不应该是和网友对掐，而是应该尽可能地回忆一下当年的真实情况。到底谁能接触到铊、谁有可能下毒、铊盐的致死量差不多一克下去要什么条件。或者能够让舆论客观的调查此事，形成压力，让警方重新调查此案，让清华在压力之下将真实的情况还原。")]),v._v(" "),_("p",[v._v("朱令被投毒，总得有个投毒者吧，不要告诉我是上帝。")])])}),[],!1,null,null,null);_.default=s.exports}}]);