(window.webpackJsonp=window.webpackJsonp||[]).push([[49],{338:function(v,_,p){"use strict";p.r(_);var t=p(14),s=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"伯利克里葬礼演说"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#伯利克里葬礼演说"}},[v._v("#")]),v._v(" 伯利克里葬礼演说")]),v._v(" "),_("blockquote",[_("p",[v._v("伯利克里")])]),v._v(" "),_("p",[v._v("过去许多在此地说过话的人，总是赞美我们在葬礼将完时发表演说的这种制度。")]),v._v(" "),_("p",[v._v("在他们看来，对于阵亡将士发表演说似乎是对阵亡战士一种光荣的表示。这一点，我不同意。我认为，"),_("strong",[v._v("这些在行动中表现自己勇敢的人，他们的行动就充分宣布了他们的光荣了")]),v._v("，正如你们刚才从这次国葬典礼中所看见的一样。")]),v._v(" "),_("p",[_("strong",[v._v("我们相信，这许多人的勇敢和英雄气概毫不因为一个人对他们说好或说歹而有所变更。")])]),v._v(" "),_("p",[v._v("首先我要说到我们的祖先们。因为在这样的典礼上，回忆他们的作为，以表示对他们的敬意，这是适当的。")]),v._v(" "),_("p",[v._v("在我们这块土地上，同一个民族的人世世代代住在这里，直到现在；因为他们的勇敢和美德，他们把这块土地当作一个自由国家传给了我们。他们无疑是值得我们歌颂的。")]),v._v(" "),_("p",[v._v("尤其是我们的父辈，更加值得我们歌颂，因为除了他们所继承的土地之外，他们还扩展了国家的领土；他们把这个国家传给我们这一代，不是没有经过流血和辛勤劳动的。")]),v._v(" "),_("p",[v._v("今天我们在这里集合的人，绝大多数正当盛年，我们已经在各方面扩充了我们国家的势力，我们无论在平时或战时，都完全能够照顾自己。")]),v._v(" "),_("p",[v._v("我不想作一篇冗长的演说来评述一些你们都很熟悉的问题，所以我不谈我们用以取得我们的势力的一些军事行动，也不谈我们的父辈英勇地抵抗我们希腊内部和外部敌人的战役。")]),v._v(" "),_("p",[v._v("我所要说的，首先是讨论我们曾经受到考验的精神，我们的宪法和使我们伟大的生活方式。说了这些之后，我想歌颂阵亡战士。我认为这种演说，在目前情况下，不会是不适当的；同时，在这里集会的全体人员，包括公民和外国人在内，听了这篇演说，也会感到有意义。")]),v._v(" "),_("p",[v._v("我要说，我们的政治制度不是从我们邻人的制度中模仿得来的。我们的制度是别人的模范，而不是我们模仿任何其他人。")]),v._v(" "),_("p",[_("strong",[v._v("我们的制度之所以被称为民主政治，是因为政权在全体公民手中，而不是在少数人手中。")])]),v._v(" "),_("p",[_("strong",[v._v("解决私人争执的时候，每个人在法律上都是平等的；让一个人担任公职优先于他人的时候，所考虑的不是某一个特殊阶级的成员，而是他具有真正的才能。")])]),v._v(" "),_("p",[_("strong",[v._v("任何人，只要他能够对国家有所贡献，就绝对不会因为贫穷而在政治上湮没无闻。")])]),v._v(" "),_("p",[v._v("正因为我们的政治生活是自由而公开的，我们彼此间的日常生活也是这样。")]),v._v(" "),_("p",[v._v("当我们隔壁邻人为所欲为的时候我们不致于因此而生气；我们也不会因此而给他以难看的颜色以伤他的情感，尽管这种颜色对他没有实际的损害。")]),v._v(" "),_("p",[v._v("在我们私人生活中，我们是自由而宽容的；但是在公家的事务中，我们遵守法律。这是因为这种法律使我们心悦诚服。")]),v._v(" "),_("p",[v._v("另外还有一点。当我们工作完毕的时候，我们可以享受各种娱乐，以提高我们的情趣。整个一年之中，有各种定期的赛会和祭祀；在我们的家庭中，我们有华丽而风雅的设备，每天怡娱心目，使我们忘记了我们的忧虑。")]),v._v(" "),_("p",[v._v("我们的城邦这样伟大，它充分地给予我们世界各地一切美好的东西，使我们享受外国的东西，就好像是我们本地的出产品一样。")]),v._v(" "),_("p",[v._v("我们是自愿地以轻松的情绪来应付危险的，而不是用那种艰苦的训练。我们的勇敢是从我们的生活方式中自然产生的，而不是因为国家法律的强迫；我认为这些是我们的优点。")]),v._v(" "),_("p",[v._v("我们不花费时间来训练自己忍受那些尚未到来的痛苦，但是当我们真的遇到痛苦的时候，我们表现出我们自己正和那些经常受到严格训练的人一样勇敢。我认为这是我们的城邦值得崇拜之处。当然还有其他的优点。")]),v._v(" "),_("p",[_("strong",[v._v("我们爱好美丽，但是没有因此而变得奢侈。")])]),v._v(" "),_("p",[_("strong",[v._v("我们爱好智慧，但是没有因此而变得柔弱。")])]),v._v(" "),_("p",[_("strong",[v._v("我们把财富当作可以适当利用的东西，而没有把它当作可以夸耀自己的本钱。至于贫穷，谁也不必以承认自己的贫穷为耻，真正的耻辱是为避免贫穷而不择手段。")])]),v._v(" "),_("p",[v._v("在我们这里，每一个人所关心的，不仅是他自己的事务，而且也关心国家的事务；就是那些最忙于他们自己的事务的人，对于一般政治也很熟悉。这是我们的特点："),_("strong",[v._v("一个不关心政治的人，我们不说他是一个只注重自己事务的人，而说他根本没有事务。")])]),v._v(" "),_("p",[v._v("我们公民们自己决定我们的政策，我们的政策也将得到广泛的讨论。我们认为言论和行动之间是没有矛盾的，最坏的事情就是没有经过恰当的讨论，就贸然开始行动；")]),v._v(" "),_("p",[v._v("这一点又是我们和其他地方的人民不同的地方。")]),v._v(" "),_("p",[v._v("我们敢于冒险，同时又能够在进行这一冒险之前深思熟虑。")]),v._v(" "),_("p",[_("strong",[v._v("他人的勇敢，是由于无知；当他们停下来思考的时候，他们就开始疑惧了。但是真正算得上勇敢的人是那个最了解人生的幸福和灾患，然后勇往直前，担当起将来会发生的事变之结果的人。")])]),v._v(" "),_("p",[v._v("再者，在关于友谊的问题上，我们和其他大多数人也形成一个明显的对比。")]),v._v(" "),_("p",[_("strong",[v._v("我们结交朋友的方法是给他人以好处，而不是从他们方面得到好处。")])]),v._v(" "),_("p",[v._v("这就使我们的友谊更为可靠，因为我们要继续对他们表示好意，使受惠于我们的人永远感激我们。但是，一些受了我们恩惠的人，在感情上缺少同样的热忱；因为他们觉得，在他们报答我们的时候，就好像是在偿还一笔债务，而不是在自觉地给予恩惠。")]),v._v(" "),_("p",[v._v("在这方面，我们是独特的。")]),v._v(" "),_("p",[v._v("当我们真正给予他人以恩惠时，我们不是因为考虑我们的得失才这样做的，而是由于我们的慷慨，我们不会因为这样做而后悔。")]),v._v(" "),_("p",[v._v("因此，如果把一切都联系起来考虑的话，我可以断言，我们的城邦是全希腊的学校；我可以断言，我们每个公民，在生活的各个方面，都能够独立自主；并且在表现独立自主的时候，能够特别地表现温文尔雅和多才多艺。")]),v._v(" "),_("p",[v._v("为了说明这并不是在这个典礼上的空洞的自我吹嘘，而是真正的具体事实，你们只要考虑一下：正因为我在上面所说的优良品质，我们的城邦才获得了它现在的势力。我们所知道的国家中，只有雅典在遇到战争的时候，可以证明它比一般人所想像的更为伟大。")]),v._v(" "),_("p",[v._v("在雅典，也只有在雅典，入侵的敌人不以战败为耻辱；受它统治的属民也不因统治者不够格而抱怨。真的，我们祖先所遗留下来的国家标志和纪念物是巨大的，不但现在，而且后世也会对我们表示赞叹。我们不需要荷马的歌颂，因为他们的歌颂只能使我们娱乐一时，而他们对于事实的估计也不足以代表真实的情况。")]),v._v(" "),_("p",[v._v("因为我们的冒险精神充溢着每个海洋和每块陆地；我们到处对我们的朋友施以恩德，对我们的敌人给予回击；关于这些事情，我们将遗留永久的纪念于后世。")]),v._v(" "),_("p",[v._v("这就是这些烈士为它慷慨而战、慷慨而死的一个城邦，因为他们只要想到丧失了这个城邦，就会不寒而栗。")]),v._v(" "),_("p",[v._v("十分自然，我们生于他们之后的人，每个人都应当忍受一切痛苦为它服务。因为这个缘故，我说了这么多话来讨论我们的城邦，因为我要很清楚的说明，我们所争取的目标比起其他那些没有我们优点的人所争取的目标要远大。因此，我想用实证来更清楚地表达我对阵亡将士们的歌颂。")]),v._v(" "),_("p",[v._v("现在对于他们的歌颂的最重要的部分，我已经说完了。")]),v._v(" "),_("p",[v._v("我已经歌颂了我们的城邦，但是，使我们的城邦光明灿烂的是这些人和类似他们的人的勇敢和英雄气概。同时，你们也会发现，言词是不能够公允地表达他们的行为的。在所有的希腊人中间，有他们这种勇气的人也不会很多。")]),v._v(" "),_("p",[v._v("在我看来，他们的那种壮烈献身，向我们表现了非凡的英雄气概，不管它是初次表现的也好，或者是最后证实的也好。他们中间有些人无疑是有缺点的，但是我们所应当记着的，首先是他们抵抗敌人、捍卫祖国的英勇行为。他们的优点抵消了他们的缺点，他们对国家的贡献多于他们在私人生活中所作的祸害。他们这些人中间，没有人因为想继续享受他们的财富而变为懦夫，也没有人逃避这个危难的日子，以图偷生脱离穷困而获得富裕。")]),v._v(" "),_("p",[v._v("他们所需要的不是这些东西，而是要挫败敌人的骄气。在他们看来，这是最光荣的冒险。他们担当了这个冒险，愿意击溃敌人，而放弃了其他一切。至于成败，他们让它留在不可预测的希望女神手中。当他们真的面临战斗的时候，他们信赖自己。")]),v._v(" "),_("p",[v._v("在战斗中，他们认为保持自己的岗位而战死比屈服而逃生更为光荣。所以他们没有受到别人的责难，而是以自己的血肉之驱抵挡了敌人的冲锋。")]),v._v(" "),_("p",[v._v("顷刻间，在他们生命的顶点，也是光荣的顶点，而又是恐惧的顶点，他们就离开我们而长逝了。")]),v._v(" "),_("p",[v._v("他们的行为是这样的勇敢，这些人无愧于他们的城邦。我们这些尚还生存的人们可以希望不会遭遇和他们同样的命运，但是在对抗敌人的时候，我们一定要有同样的勇敢精神。")]),v._v(" "),_("p",[v._v("这不是单纯从理论上估计优点的问题。关于击败敌人的好处，我可以说得很多（这些，你们和我一样都是知道的）。我宁愿你们每天把眼光集中到雅典的伟大上。")]),v._v(" "),_("p",[_("strong",[v._v("它真正是伟大的，你们应当热爱它。")])]),v._v(" "),_("p",[v._v("当你们认识到它的伟大时，再回忆一下，使它伟大的是有冒险精神的人们，知道他们的责任的人们，深以不达到某种目标为耻辱的人们。")]),v._v(" "),_("p",[v._v("如果他们在一项事业上失败了，他们也会下定决心，不让他们的城邦发现他们缺乏勇敢，他们尽可能把最好的东西贡献给了国家。他们贡献了他们的生命给国家和我们全体，至于他们自己，则获得了永远常青的赞美，最光辉灿烂的坟墓? 不是他们遗体所安葬的坟墓，而是他们的光荣永远留在人们的心中，每到适当的时机，总会激动着他人的言论或行动。")]),v._v(" "),_("p",[v._v("整个地球其实都是他们的纪念物；他们的纪念物不仅是在自己的祖国，而且也在外国；他们的英名已经生根在人们的心中，而不是凿刻在有形的石碑上。你们应该努力学习他们的榜样。你们要下定决心：要自由，才能有幸福；要勇敢，才能有自由。现在依照法律上的要求，我已经说了我所应当说的话。刚才我们已对死者作了祭献。")]),v._v(" "),_("p",[v._v("将来他们的儿女们将由公费抚养，直到他们达到成年为止。这是国家给予死者和他们的儿女们的花冠和奖品，作为他们经得住考验的酬谢。")]),v._v(" "),_("p",[v._v("凡是对于勇敢有最大奖赏的地方，就可以找到人民中间最优秀和最勇敢的精神。")]),v._v(" "),_("p",[v._v("现在你们对于阵亡的亲属已致哀吊，你们可以散开了。")])])}),[],!1,null,null,null);_.default=s.exports}}]);