(window.webpackJsonp=window.webpackJsonp||[]).push([[67],{356:function(v,_,p){"use strict";p.r(_);var t=p(14),s=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"我心目中的伟大作品"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#我心目中的伟大作品"}},[v._v("#")]),v._v(" 我心目中的伟大作品")]),v._v(" "),_("blockquote",[_("p",[v._v("残雪")])]),v._v(" "),_("p",[v._v("我心目中的伟大作品，是那些具有永恒性的作品。即这类作家的作品无论经历多少个世纪的轮回，依然不断地得到后人的解释，使后人产生新感受。这样的作家身上具有“神性”，有点类似于先知。")]),v._v(" "),_("p",[v._v("就读者的数量来说，这类作品不能以某段时间里的空间范围来衡量，有时甚至由于条件的限制，一开始竟被埋没。但终究，他们的读者远远超出那些通俗作家。")]),v._v(" "),_("p",[_("strong",[v._v("人类拥有一条隐秘的文学史的长河，这条河在最深最黑暗的地底，她就是由这些描写本质的作家构成的。")])]),v._v(" "),_("p",[_("strong",[v._v("她是人类多少个世纪以来进行纯精神追求的镜子。")])]),v._v(" "),_("p",[v._v("我不喜欢“伟大的中国小说”这个提法，其内涵显得小里小气。如果作家的作品能够反映出人的最深刻、最普遍的本质（这种东西既像粮食、天空，又像岩石和大海），那么无论哪个种族的人都会承认她是伟大的作品——当然这种承认经常不是以短期效应来衡量的。")]),v._v(" "),_("p",[v._v("对于我来说，作品的地域性并不重要，谁又会去注意莎士比亚的英国特色、但丁的意大利特色呢？如果你达到了深层次的欣赏，地域或种族完全可以忽略不计。")]),v._v(" "),_("p",[v._v("说到底，文学不就是人作为人为了认识自己而进行的高级活动吗？")]),v._v(" "),_("p",[v._v("作家可以从地域的体验起飞（大概任何人都免不了要这样做），但决不应该停留在地域这个表面的经验之上，有野心的作家应该有更深、更广的追求。而停留在表面经验正是中国作家（以及当今的美国作家）的致命伤。由于过分推崇自己民族的传统，他们看不到或没有力量进入深层次的精神领域。这就使得作品停留在所谓“民族经验”“写实”的层次上，这样的作品的生命力必然是短暂的，其批判的力度也是可疑的（这只要看看当今中国大陆文化人的普遍倒退和堕落，看看多数美国人民对于伊战的狂热，以及历来对于战争的狂热就可以得出佐证）。")]),v._v(" "),_("p",[_("strong",[v._v("伟大的作品都是内省的、自我批判的。")])]),v._v(" "),_("p",[v._v("在我的明星列表中，有这样一些作家：荷马、但丁、弥尔顿、莎士比亚、塞万提斯、歌德、卡夫卡、博尔赫斯、卡尔维诺、圣·德克旭贝里、托尔斯泰、果戈理、陀思妥耶夫斯基等等。")]),v._v(" "),_("p",[v._v("这个名单中的主流是西方人和具有西方观念的作家，因为我认为文学的源头就在西方，而中国，从一开始文学就不是作为独立的精神产物而存在。")]),v._v(" "),_("p",[v._v("中国文学自古以来缺少文学最基本的特征——人对自身本质的自觉的认识。也就是说，中国文学彻底缺少自相矛盾，并将这矛盾演绎到底的力量和技艺。")]),v._v(" "),_("p",[v._v("传统的文学从来都是依附的，向外（即停留在表层）的。即使是《红楼梦》那样的伟大作品，在今天看来也已经很大程度上过时了，因为并不能促使人自省和奋发向上，对于人心的描述也过于浮浅，没有涉及内心矛盾，相当于关于人的幼年的文学。")]),v._v(" "),_("p",[v._v("鲁迅先生写过一些伟大的作品（“野草”全部和“故事新编”中的一些），但数量太少太少，文化对于他的压迫使他未来得及发展自己的天才。在这个意义上，大陆的文学始终处在危机之中，探讨深层次人性，提升国民性的作品远没有形成潮流。")]),v._v(" "),_("p",[v._v("在我看来，中国的作家如果不能战胜自己的民族自恋情结，就无法继续追求文学的理想。\n所以在大陆的文坛，很多作家到了四十来岁就开始退化，要么写不出作品，要么用赝品来敷衍，蒙骗读者。这种现象产生的根源在于民族自大的心理。")]),v._v(" "),_("p",[v._v("我们的文化摧毁、毒害了我们的天才。中国文化中精神的缺失导致当今的大陆文学不能生长、发育，就像一些长着娃娃脸的小老头，永远是那么的老于世故，永远能够自圆其说，具有世界上最出色的匠人的精明，却唯独没有内省，没有对于自身的批判。")]),v._v(" "),_("p",[v._v("在所有涉及自身的方面，大部分大陆作家都或者用一些白日梦来加以美化，或者用古代文化提倡的虚无主义来化解矛盾。")]),v._v(" "),_("p",[v._v("没有精神追求的文学是伪文学，描写表面的经验的文学则是浅层次的文学。这在当今的文学发展中好像是个世界性的问题。物质世界的飞速发展已经使得大部分作家越来越懒，越来越满足于一些表面经验，而读书的人，也在一天天减少。")]),v._v(" "),_("p",[v._v("据说实验小说在日本这样的国家已经很难得到出版了，而集体自杀的事件在这个国家倒屡屡发生；又据说连在德国，这个思想之父的国度里，人们也不看实验小说了。")]),v._v(" "),_("p",[v._v("幻灭感如同黑色的幽灵在世界游荡。然而我仍然相信，那条隐秘的长河是不会断流的，尽管历史有高潮也有低谷。任何时代总会有那么一小部分人，以自己默默的劳动为那条河流注入新的活力。延续了几千年的理想还将延续下去，同这个浮躁、浅薄而喧闹的世界对抗。")]),v._v(" "),_("p",[v._v("伟大的作品都是彻底个人化的，因为人只能在真正个人化的写作中达到自由。不在写作的瞬间抛开一切物质的累赘，不同物质划清界线，灵感就无法起飞。而这种活动力图达到的就是个人的人格独立。要做到这一点，对于一位中国大陆作家是特别困难、特别需要勇气的事。")]),v._v(" "),_("p",[v._v("文学的实践就是这样一种操练。像西方作家但丁或歌德那样来认识、拯救自身，并将其作为最高的目标的人在大陆太少太少。一谈及文学，人们就理所当然地认为只同表面的经验、“共同的”现实有关，接下去自然就只能涉及如何样完善表达的“技巧”，如何样将陈词滥调弄出些“新意”的问题了。")]),v._v(" "),_("p",[v._v("文坛上很有一些高调的理论家，提出要肃清纯文学的影响，大力提倡所谓“现实关怀”。且不说此处对于“纯文学”的定义含混滑稽，所谓“现实关怀”这种陈词滥调我们已经听了好几十年，实在是同真正的文学无关。只有个别作家注意到了在我们的经验世界之外，还存在着另一个广大得多的世界等待着我们去遭遇，去开拓。")]),v._v(" "),_("p",[v._v("我认为，作为世界上最古老的文明的拥有者，中国作家在这方面本应是得天独厚的。问题只在于你是否能战胜自己的文化的惰性，从另一种文化中去获取这种开拓的工具。我们不去开拓，那个广大无边的领域就根本不属于我们。")]),v._v(" "),_("p",[v._v("一位作家，不论他用什么方法写作，只要他有认识自我的好奇心，改造自我的冲动，有开阔的胸怀，就一定会进入人性探索的深层领域，将那个古老的矛盾进行我们历史上从未有过的演绎，在自救的同时影响读者，改造国民性。")]),v._v(" "),_("p",[v._v("伟大的文学并不是高不可攀的，她就属于踏踏实实地追求的作家，她的内核就是人的本质。每一位能在文学创造中将理想尽力发挥的作家在写作的瞬间都是伟大的作家，这样写出的作品则是伟大的作品。当然各人的先天能力有大小，能否成为明星并不重要，只要处在伟大的追求境界中去完成自己，就是最大的幸福。")]),v._v(" "),_("p",[v._v("我想用莎士比亚的话来结束这篇文章:")]),v._v(" "),_("p",[v._v("“上帝造我们，给我们这么多智慧/使我们能瞻前顾后，绝不是要我们/把这种智能，把这种神明的理性/霉烂了不用啊。”（摘见《哈姆雷特》）")])])}),[],!1,null,null,null);_.default=s.exports}}]);