(window.webpackJsonp=window.webpackJsonp||[]).push([[75],{364:function(t,v,_){"use strict";_.r(v);var s=_(14),e=Object(s.a)({},(function(){var t=this,v=t._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[v("h1",{attrs:{id:"水畔听钟七十年"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#水畔听钟七十年"}},[t._v("#")]),t._v(" 水畔听钟七十年")]),t._v(" "),v("blockquote",[v("p",[t._v("江南")])]),t._v(" "),v("p",[t._v("成帝元年八月十三日。")]),t._v(" "),v("p",[t._v("夜深寂寥，隔着水面，文庙的镇国钟轰然响起，钟声在微凉的夜里传出很远，凤凰池上水波潋滟，一轮月影破碎开来。")]),t._v(" "),v("p",[t._v("“文庙听钟”、“武庙看剑” 是初到南淮的世家子弟一定要做的两件事，文庙里供奉着七百年前蔷薇皇帝赐予百里氏的巨大铜钟，而武庙里是百里氏祖先追随皇帝征战时的佩剑。只不过七百年过去，文庙之钟武庙之剑都再也没有昔日的沙场气息，战争始终没有再侵入繁华的南淮，夏夜的月下，一切都变得柔媚如水。")]),t._v(" "),v("p",[t._v("百里氏出名的文睿国主毕生钻研诗歌，最喜欢趁夜驱赶马车，停在凤凰池边的岳桥上听钟，眺望远方刺天的高塔影子，独自喃喃。")]),t._v(" "),v("p",[t._v("他身为国主而有倾世之才，随笔就在桥上把想到的诗句写在纸上，再一张一张折成纸船，船里放上一截宫里点剩的蜡烛头，星火一点，借着桥下流水放向远方。下游远处夜夜都有一群人不合眼地候着，去捡那些纸船，运气好的时候水没有污掉墨迹，在文庙的集市上可售上千金铢。")]),t._v(" "),v("p",[t._v("后来《文睿传灯歌》的集子，就是从文睿国主这些纸船上搜集起来的。")]),t._v(" "),v("p",[t._v("文睿国主死在七十岁上的时候，死在了岳桥上。内监们在远处看着老去的国主颤巍巍地放下一只纸船，坐在涨水的岸边濯洗双足，从此就再也没有站起来。下游的人拾到的最后一只纸船上写着：“水畔听钟七十年，便了却了此生。”")]),t._v(" "),v("p",[t._v("许多年后再来岳桥的人，听着文庙的钟声，多半都不是在想那古老的铜钟本是一座警钟，而是追思水畔听钟七十年后安然辞别的洒脱。")])])}),[],!1,null,null,null);v.default=e.exports}}]);